mapper.update:
UPDATE tb_select_subject
SET status = #{studentStatus}
WHERE subject_id = #{subjectId}

mapper.select:
SELECT *
FROM tb_select_subject
WHERE student_id = #{studentId} AND subject_id = #{subjectId}

mapper.delete:
DELETE
FROM tb_select_subject
WHERE student_id = #{studentId} AND subject_id = #{subjectId}
